import { useState } from "react";

function Rechercher({ data }) {

    const [donnee,setDonnee] = useState()
    const [donnees,setDonnees] = useState([])


    function chercher(){
        let newrow = data.filter((item)=>{
            return item.service.nomser == donnee
        })
        console.log(newrow)
        setDonnees(...donnees,newrow)
        console.log(donnees)
    }

  return (
    <>
      <div>
        <h1> Rechercher par service </h1>

        <label htmlFor="">entrer le nom du service</label>
        <input type="text" onChange={(e)=>{setDonnee((e.target.value).toLowerCase())}}/>

        <button onClick={chercher}>chercher</button>

        <ul>
            {donnees.map((item)=>{
                <li>Nom : {item.nomser} Prenom : {item.prenomser}</li>
            })}
        </ul>
      </div>
    </>
  );
}

export default Rechercher;
