import { useState } from "react"

function Voitures(){

    const [matricule,setMatricule] = useState("")
    const [marque,setMarque] = useState("")
    const [date,setDate] = useState("")
    const [couleur,setCouleaur] = useState("")

    const [voiture,setVoiture] = useState({
        matricule:"",
        marque:"",
        date:"",
        couleur:""
    })

    function confirm() {
        setVoiture({...voiture,matricule:matricule,marque:marque,date:date,couleur,couleur})
    }

    return (
        <>
            <div>
                <label htmlFor="">Matricule :</label>
                <input type="text" onChange={(e)=>setMatricule({matricule:e.target.value})}/>

                <label htmlFor="">Marque :</label>
                <input type="date" onChange={(e)=>setMarque({...voiture,marque:e.target.value})}/>

                <label htmlFor="">Date de mise en circulation :</label>
                <input type="text" onChange={(e)=>setDate({...voiture,date:e.target.value})}/>

                <label htmlFor="">Couleur:</label>
                <input type="text" onChange={(e)=>setCouleur({...voiture,couleur:e.target.value})}/>

                <button onClick={confirm}>Confirm</button>
            </div>

            <h1>Recapitulatif des informations :</h1>
                <ul>
                    <li>{voiture.matricule}</li>
                    <li>{voiture.marque}</li>
                    <li>{voiture.date}</li>
                    <li>{voiture.couleur}</li>
                </ul>
        </>
    )

}

export default Voitures
