            <ul className="mt-4 space-y-2 text-gray-700">
                {donnees.map((item, index) => (
                    <li key={index} className="border-b border-gray-200 pb-2">
                        <strong>Nom:</strong> {item.nomsal} <strong>Prénom:</strong> {item.prenomsal}
                    </li>
                ))}
            </ul>
        </div>
    );
}

