import { useState } from "react"

function App() {

    const [code,setCode] = useState()
    const [marque,setMaruqe] = useState()
    const [date,setDate] = useState()
    const [categorie,setCategorie] = useState()
    const [materiel,setMateriel] = useState({
        code : "",
        marque : "",
        date : "",
        categorie : ""
    })

    function confirmer(){
        setMateriel({materiel,code:code,})
    }

    return (
        <>
            <label htmlFor="">Code Materiel</label>
            <input type="text" onChange={(e)=>setCode(e.target.value)} />

            <label htmlFor="">Marque</label>
            <select name="" id="" onChange={(e)=>setMaruqe(e.target.value)}>
                <option value="hp">HP</option>
                <option value="dell">Dell</option>
            </select>
            
            <label htmlFor="">Date debut Utilisation</label>
            <input type="date" onChange={(e)=>setDate(e.target.value)} />

            <label htmlFor="">Categorie</label>
            <input type="text" onChange={(e)=>setCategorie(e.target.value)} />

            <button>confirmer</button>

            <h1>Recapitukatif des informations :</h1>
            <ul>
                {materiel.map((item)=>{
                    return (
                        <>
                            <li>{item.code}</li>
                            <li>{item.marque}</li>
                            <li>{item.date}</li>
                            <li>{item.categorie}</li>
                        </>
                    )
                })}
            </ul>
        </>
    )
}

export default App
