import { useState } from "react";

function Rechercher({info}){

    const [donnee, setDonnee] = useState("");
    const [donnees, setDonnees] = useState([]);
    const [message,setMessage] = useState("");

    function chercher() {
        console.log(info)
        let newRow = info.filter((item)=> item.departement.nomDep === donnee) 
        
        if (newRow){
            setMessage("")
        }else {
            setMessage("")
        }
        setDonnees(newRow)
    }

    return (
        <>
            <div>
                <h1></h1>
                    <label htmlFor=""></label>
                    <input type="text" onChange={(e)=>setDonnee(e.target.value.toUpperCase())}/>

                <button onClick={chercher}></button>

                <h2></h2>
                <span>{message}</span>
                <ul>
                    {donnees.map((item,index)=>{
                        return (
                            <li key={index}>Nom : {item.nomEmp} Prenom : {item.prenomEmp}</li>
                        )
                    })}
                </ul>
            </div>  
        </>
    )
}

export default Rechercher
