import { useState } from "react";

function Formulaire() {
  const [theme,setTheme] = useState("")
  const [dateDebut,setDateDebut] = useState("")
  const [dateFin,setDateFin] = useState("")
  const [coutJournalier,setCoutJournalier] = useState(0)
  const [duree,setDuree] = useState(0)
  const [formulaire, setFormulaire] = useState({
    theme: "",
    data_debut: "",
    data_fin: "",
    cout_journalier: 0,
    duree: 0,
  });

  function confirm(){
    setFormulaire({...formulaire,theme:theme,data_debut:dateDebut,data_fin:dateFin,cout_journalier:coutJournalier,duree:duree})
  }

  return (
    <>
        <div>
            <label htmlFor="theme">Theme</label>
            <input type="text" onChange={(event)=>(setTheme(event.target.value))} />

            <label htmlFor="">Date Debut</label>
            <input type="text" onChange={(event)=>(setDateDebut(event.target.value))} />

            <label htmlFor="">Date Fin</label>
            <input type="text" onChange={(event)=>(setDateFin(event.target.value))} />

            <label htmlFor="">Cout Journalier</label>
            <input type="text" onChange={(event)=>setCoutJournalier(event.target.value)} />

            <label htmlFor="">Duree</label>
            <input type="text" onChange={(event)=>setDuree(event.target.value)} />

            <button onClick={confirm}>Confirm</button>

            <p>
                theme : {formulaire.theme}
                data_debut : {formulaire.data_debut}
                data_fin : {formulaire.data_fin}
                description : {formulaire.description}
                cout_journalier: {formulaire.cout_journalier}
                duree : {formulaire.duree}
            </p>

        </div>
    </>
  )
}

export default Formulaire;
