import { useState } from "react";

function Formulaire() {
  const [formulaire, setFormulaire] = useState({
    theme: "",
    data_debut: "",
    data_fin: "",
    description: "",
    cout_journalier: 0,
    duree: 0,
  });
  const [formulaires,setFormulaires] = useState([])

  function confirm(){
    setFormulaires({...formulaires,formulaire})
  }

  return (
    <>
        <div>
            <label htmlFor="theme"></label>
            <input type="text" onChange={(event)=>setFormulaire({...formulaire,theme:event.target.value})} />

            <label htmlFor=""></label>
            <input type="text" onChange={(event)=>setFormulaire({...formulaire,data_debut:event.target.value})} />

            <label htmlFor=""></label>
            <input type="text" onChange={(event)=>setFormulaire({...formulaire,data_fin:event.target.value})} />

            <label htmlFor=""></label>
            <input type="text" onChange={(event)=>setFormulaire({...formulaire,description:event.target.value})} />

            <label htmlFor=""></label>
            <input type="text" onChange={(event)=>setFormulaire({...formulaire,cout_journalier:event.target.value})} />

            <label htmlFor=""></label>
            <input type="text" onChange={(event)=>setFormulaire({...formulaire,duree:event.target.value})} />

            <button onClick={confirm}>confirm</button>

            <p>
                theme : {formulaires.formulaire.theme}
                data_debut : {formulaires.formulaire.data_debut}
                data_fin : {formulaires.formulaire.data_fin}
                description : {formulaire.description}
                cout_journalier: {formulaire.cout_journalier}
                duree : {formulaire.duree}
            </p>

        </div>
    </>
  )
}

export default Formulaire;
