function Evenements({ expretsData }) {
    // Assuming we want the first item in expretsData
    const evenements = expretsData[0].evenements;

    // Calculate the total cost of all events
    const totalCost = evenements.reduce((acc, item) => {
        const eventTotal = item.cout_journalier * item.duree;
        return acc + eventTotal;
    }, 0);

    return (
        <>  
            <table>
                <thead>
                    <tr>
                        <th>Theme</th>
                        <th>Date de debut</th>
                        <th>Date de fin</th>
                        <th>Description</th>
                        <th>Cout journalier</th>
                        <th>Duree</th>
                        <th>Coût Total de l'Événement</th>
                    </tr>
                </thead>
                <tbody>
                    {evenements.map((item, index) => (
                        <tr key={index}>
                            <td>{item.theme}</td>
                            <td>{item.data_debut}</td>
                            <td>{item.data_fin}</td>
                            <td>{item.description}</td>
                            <td>{item.cout_journalier}</td>
                            <td>{item.duree}</td>
                            <td>{item.cout_journalier * item.duree}</td>
                        </tr>
                    ))}
                    <tr>
                        <td colSpan="6" style={{ fontWeight: 'bold', textAlign: 'right' }}>Total des coûts des événements assurés est :</td>
                        <td style={{ fontWeight: 'bold' }}>{totalCost}</td>
                    </tr>
                </tbody>
            </table>
        </>
    );
}

export default Evenements;
