import { useEffect, useState } from "react";

function  ComponentStagiaires(){
    let Stagiaires=[{id :1,matricule :1454,nom : "Alaoui ",codepostal :20400,ville :"casa",moyenne :12.56},
    {id :2, matricule :1455,nom : "Mansouri ",codepostal :20400,ville :"casa",moyenne :14.67}];

    let [stagiaire,setStagiaire] = useState({
        id :null,
        matricule :0,
        nom : "",
        codepostal :0,
        ville :"",
        moyenne :0
    })

    // let [message,setMessage] = useState("")

    let [listStagiaires,setListStagiaires] = useState([])

    useEffect(()=>{
        setListStagiaires(Stagiaires)
    },[])

    // function sendMessage(){
    //     if (listStagiaires.length === 0) {
    //         setMessage("tableau des stagiaires vide")
    //     }else{
    //         setMessage("")
    //     }
    // }

    function supprimer(){
        let newStagiaires = listStagiaires.filter((item)=>{
            return item.id !== stagiaire.id
        })
        setListStagiaires(newStagiaires)
    }

    function editer(){
        let editedStagiaires = listStagiaires.map((item)=>{
            if(stagiaire.id === item.id){

            }
        })
    }

    function stagiaireId(){
        return listStagiaires.length + 1
    }

    function ajouter(){
        const newStagire = {...stagiaire,id:stagiaireId()}
        setListStagiaires([...listStagiaires,newStagire])
    }

    return (
        <>
            <div>
                <input type="number" placeholder="id" required value={stagiaireId()}/>
                <input type="number" placeholder="matricule" required value={stagiaire.matricule} onChange={(e)=>setStagiaire({...stagiaire,matricule:e.target.value})}/>
                <input type="text" placeholder="nom" required value={stagiaire.nom} onChange={(e)=>setStagiaire({...stagiaire,nom:e.target.value})}/>
                <input type="number" placeholder="codepostal" required value={stagiaire.codepostal} onChange={(e)=>setStagiaire({...stagiaire,codepostal:e.target.value})}/>
                <input type="text" placeholder="ville" required value={stagiaire.ville} onChange={(e)=>setStagiaire({...stagiaire,ville:e.target.value})}/>
                <input type="number" placeholder="moyenne" required value={stagiaire.moyenne} onChange={(e)=>setStagiaire({...stagiaire,moyenne:e.target.value})}/>

                <button onClick={ajouter}>Ajouter</button>
            </div>

            <table>
                <thead>
                    <th>id</th>
                    <th>matricule</th>
                    <th>Nom</th>
                    <th>ville</th>
                    <th>code Postal</th>
                    <th>Moyenne</th>
                    <th>supprimer</th>
                    <th>Editer</th>
                </thead>

                <tbody>
                    {listStagiaires.map((item,index)=>{
                        return  (
                            <>
                                <tr key={index} onClick={() => setStagiaire({...item})}>
                                    <td>{item.id}</td>
                                    <td>{item.matricule}</td>
                                    <td>{item.nom}</td>
                                    <td>{item.codepostal}</td>
                                    <td>{item.ville}</td>
                                    <td>{item.moyenne}</td>
                                    <td><button onClick={supprimer}>supprimer</button></td>
                                    <td><button onClick={editer}>editer</button></td>
                                </tr>                            
                            </>
                        )
                    })}



                </tbody>

            </table>
            {/* <span>{message}</span> */}
        </>
    )
}

export default ComponentStagiaires;
