import { useEffect, useState } from "react";

function  Stagiaires(){
    let Stagiaires=[{id :1,matricule :1454,nom : "Alaoui ",codepostal :20400,ville :"casa",moyenne :12.56},
    {id :2, matricule :1455,nom : "Mansouri ",codepostal :20400,ville :"casa",moyenne :14.67}];

    let [stagiaire,setStagiaire] = useState({
        id :0,
        matricule :0,
        nom : "",
        codepostal :0,
        ville :"",
        moyenne :0
    })

    let [listStagiaires,setListStagiaires] = useState([])

    useEffect(()=>{
        setListStagiaires(Stagiaires)
    },[])

    function stagiaireId(){
        return listStagiaires.length + 1
    }

    function ajouter(){
        setListStagiaires([...listStagiaires,stagiaire])
    }

    return (
        <>
            <div>
                <input type="text" placeholder="id" required value={stagiaireId()}/>
                <input type="text" placeholder="matricule" required/>
                <input type="text" placeholder="nom" required/>
                <input type="text" placeholder="codepostal" required/>
                <input type="text" placeholder="ville" required/>
                <input type="text" placeholder="moyenne" required/>

                <button onClick={ajouter}>Ajouter</button>
            </div>

            <table>
                <thead>
                    <th>id</th>
                    <th>matricule</th>
                    <th>Nom</th>
                    <th>ville</th>
                    <th>code Postal</th>
                    <th>Moyenne</th>
                    <th>supprimer</th>
                    <th>Editer</th>
                </thead>

                <tbody>
                    {listStagiaires.map((item,index)=>{
                        return  (
                            <>
                                <tr key={index}>
                                    <td>{item.id}</td>
                                    <td>{item.matricule}</td>
                                    <td>{item.nom}</td>
                                    <td>{item.codepostal}</td>
                                    <td>{item.ville}</td>
                                    <td>{item.moyenne}</td>
                                </tr>                            
                            </>
                        )
                    })}



                </tbody>

            </table>
        </>
    )
}

export default Stagiaires;
